module.exports.user = function (mongoose) {
  var Schema = mongoose.Schema;

  /* schema for user */
  userSchema = new Schema ({
    name: String,
    email: String, 
  }, { versionKey: false });

  /* methods for userSchema */

  // userSchema.method({
  //   example: function () {
  //     return true;
  //   }
  // });

  return mongoose.model('user', userSchema);
};
